mapper.select:
   	    select 
    
    from t_earthquake
    
        
            AND eqname like '%${eqname}%' 
        
        
            AND state = #{state,jdbcType=VARCHAR}
        
        
            AND eventid = #{eventid,jdbcType=VARCHAR}
        
         
            AND province like '%${province}%' 
        
    
    order by createtime desc
   	     
			LIMIT #{start} ,#{limit}
