mapper.select:
   	    select 
    
    from t_hbplan_detail
    
        
            AND hbplanid = #{hbplanid,jdbcType=INTEGER}
        
        
            AND type = #{type,jdbcType=VARCHAR}
        
    
    order by cid asc
   	     
			LIMIT #{start} ,#{limit}

mapper.delete:
delete from t_hbplan_detail
where hbplanid = #{hbplanid,jdbcType=INTEGER}
and type = #{type,jdbcType=VARCHAR}
