mapper.select:
   	    select 
    
    from t_link
    
        
            AND name = #{name,jdbcType=VARCHAR}
        
        
            AND state = #{state,jdbcType=VARCHAR}
        
        
            AND eqcompany = #{eqcompany,jdbcType=VARCHAR}
        
        
            AND rid = #{rid,jdbcType=INTEGER}
        
    
    order by state desc,id asc
   	     
			LIMIT #{start} ,#{limit}
